import React, { useState, useEffect } from "react";
import { FaAppleAlt, FaRunning, FaBed, FaSmile, FaHeartbeat, FaHandsWash, FaBrain, FaBan } from "react-icons/fa";
import "./HealthTips.css"; // Import the CSS file

const healthTips = [
  { title: "Maintain a Balanced Diet", description: "Eat a variety of fruits, vegetables, whole grains, and lean proteins. Stay hydrated by drinking enough water.", icon: <FaAppleAlt />, bgColor: "card-green", img: "https://media.istockphoto.com/id/1457433817/photo/group-of-healthy-food-for-flexitarian-diet.jpg?s=612x612&w=0&k=20&c=v48RE0ZNWpMZOlSp13KdF1yFDmidorO2pZTu2Idmd3M=" },
  { title: "Exercise Regularly", description: "Engage in at least 30 minutes of moderate exercise daily, such as walking, jogging, or yoga.", icon: <FaRunning />, bgColor: "card-blue", img: "https://sa1s3optim.patientpop.com/filters:format(webp)/assets/images/provider/photos/2503470.jpg" },
  { title: "Prioritize Quality Sleep", description: "Aim for 7-9 hours of sleep per night. Maintain a consistent sleep schedule and reduce screen time before bed.", icon: <FaBed />, bgColor: "card-purple", img: "data:image/jpeg;base64," },
  { title: "Manage Stress Effectively", description: "Practice meditation, deep breathing, or mindfulness. Engage in hobbies that help you relax.", icon: <FaSmile />, bgColor: "card-yellow", img: "https://i.pinimg.com/236x/4a/4f/61/4a4f61cff1c5c9cda4ccb1bc8dbfe0b4.jpg" },
  { title: "Avoid Harmful Habits", description: "Quit smoking and limit alcohol consumption. Avoid excessive caffeine and junk food.", icon: <FaBan />, bgColor: "card-red", img: "data:image/jpeg;base64," },
  { title: "Regular Health Checkups", description: "Visit your doctor for routine checkups and screenings. Get vaccinations as recommended.", icon: <FaHeartbeat />, bgColor: "card-pink", img: "https://i.pinimg.com/474x/e5/9b/68/e59b68a59f8f9ce5fabf6c782e5f2fe6.jpg" },
  { title: "Maintain Good Hygiene", description: "Wash hands frequently with soap and water. Keep your surroundings clean to prevent infections.", icon: <FaHandsWash />, bgColor: "card-teal", img: "https://i.pinimg.com/236x/7e/f5/56/7ef5562e914e31786069adfa7f791f0a.jpg" },
  { title: "Take Care of Your Mental Health", description: "Stay socially connected with friends and family. Seek professional help if experiencing stress.", icon: <FaBrain />, bgColor: "card-orange", img: "https://i.pinimg.com/236x/27/0a/1e/270a1e5da30a59df4f257d60e1742d38.jpg" },
];

const HealthTips = () => {
  const [isInView, setIsInView] = useState(false);

  // Track scroll and add fade-in class when in view
  useEffect(() => {
    let lastScrollTop = 0;
    const handleScroll = () => {
      const elements = document.querySelectorAll('.fade-in');
      elements.forEach((el) => {
        const rect = el.getBoundingClientRect();
        if (rect.top <= window.innerHeight && rect.bottom >= 0) {
          el.classList.add('show');
        }
      });
    };
    
    window.addEventListener('scroll', handleScroll);
    handleScroll(); // Call immediately to check for already visible items

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="container">
      <h2>🌿 Essential Health Tips</h2>
      <div className="grid">
        {healthTips.map((tip, index) => (
          <div key={index} className={`card ${tip.bgColor} fade-in`}>
            <div className="card-content">
              {/* Image comes from the left */}
              <img src={tip.img} alt={tip.title} />
              {/* Text and description come from the right */}
              <div className="text">
                <div className="flex items-center">
                  <div className="card-icon">{tip.icon}</div>
                  <h3>{tip.title}</h3>
                </div>
                <p>{tip.description}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HealthTips;
